INSERT INTO times(id, nome, pais_origem, estado_origem, cidade_origem, ano_fundacao, conferencia_nba, divisao_nba, arena_id, website_oficial, redes_sociais, historico) VALUES (1, 'Atlanta Hawks', 'Estados Unidos', 'Geórgia', 'Cidade de Atlanta', 1946, 'Conferência Leste', 'Divisão Sudeste', 1, 'https://www.nba.com/hawks/',
'Twitter: https://twitter.com/ATLHawks
Facebook: https://www.facebook.com/atlhawks/
Instagram: https://www.instagram.com/atlhawks/
YouTube: https://www.youtube.com/user/atlhawks
TikTok: https://www.tiktok.com/@atlhawks', 'O Atlanta Hawks é uma equipe de basquete profissional dos Estados Unidos, que foi fundada em 1946 e atualmente compete na Conferência Leste da NBA. A equipe tem uma história rica, tendo conquistado quatro títulos de divisão, um título de conferência e um título de campeonato em sua história. Alguns dos jogadores mais famosos que já jogaram pelo Atlanta Hawks incluem Dominique Wilkins, Bob Pettit e Dikembe Mutombo. A equipe é conhecida por seu estilo de jogo rápido e ofensivo, e por sua leal base de fãs em Atlanta e em todo o mundo.');

INSERT INTO times(id, nome, pais_origem, estado_origem, cidade_origem, ano_fundacao, conferencia_nba, divisao_nba, arena_id, website_oficial, redes_sociais, historico) VALUES(2, 'Boston Celtics', 'Estados Unidos', 'Massachusets', 'Boston', 1946, 'Conferência Leste', 'Divisão Atlântico', 2, 'www.nba.com/celtics/',
'Twitter: https://twitter.com/celtics
Facebook: https://www.facebook.com/bostonceltics
Instagram: https://www.instagram.com/celtics
YouTube: https://www.youtube.com/user/Celtics
TikTok: https://www.tiktok.com/@celtics','O Boston Celtics é uma das franquias mais bem sucedidas da história da NBA, com 17 títulos de campeão da NBA, incluindo um período de dominação nos anos 60, quando conquistou 11 títulos em 13 anos. O time também teve grandes jogadores em sua história, como Bill Russell, Larry Bird e Paul Pierce. Atualmente, o Celtics é um dos times mais populares e competitivos da liga.');

INSERT INTO times(id, nome, pais_origem, estado_origem, cidade_origem, ano_fundacao, conferencia_nba, divisao_nba, arena_id, website_oficial, redes_sociais, historico) VALUES (3, 'Broonklyn Nets', 'Estados Unidos', 'Nova York', 'Brooklyn', 1967, 'Conferência Leste', 'Divisão do Atlântico', 3, 'https://www.nba.com/nets/',
