INSERT INTO times(id, nome, pais_origem, estado_origem, cidade_origem, ano_fundacao, conferencia_nba, divisao_nba, arena_id, website_oficial, redes_sociais, historico) VALUES (8, 'Denver Nuggets', 'Estados Unidos', 'Colorado', 'Denver', 1967, 'Conferência Oeste', 'Divisão Noroeste', 8, 'https://www.nba.com/nuggets/', 'Twitter: https://twitter.com/nuggets
