INSERT INTO times(id, nome, pais_origem, estado_origem, cidade_origem, ano_fundacao, conferencia_nba, divisao_nba, arena_id, website_oficial, redes_sociais, historico) VALUES (9, 'Detroit Pistons', 'Estados Unidos', 'Michigan', 'Detroit', 1941, 'Conferência Leste', 'Divisão Central', 9, 'https://www.nba.com/pistons',
