INSERT INTO times(id, nome, pais_origem, estado_origem, cidade_origem, ano_fundacao, conferencia_nba, divisao_nba, arena_id, website_oficial, redes_sociais, historico) VALUES (10, 'Golden State Warriors',  'Estados Unidos', 'Califórnia', 'San Francisco', 1946, 'Conferência Oeste', 'Divisão Pacífico', 10, 'https://www.nba.com/warriors/',
