INSERT INTO times(id, nome, pais_origem, estado_origem, cidade_origem, ano_fundacao, conferencia_nba, divisao_nba, arena_id, website_oficial, redes_sociais, historico) VALUES (11, 'Houston Rockets', 'Estados Unidos', 'Texas', 'Houston', 1967, 'Conferência Oeste', 'Divisão Sudoeste', 11, 'https://www.nba.com/rockets/',
