INSERT INTO times(id, nome, pais_origem, estado_origem, cidade_origem, ano_fundacao, conferencia_nba, divisao_nba, arena_id, website_oficial, redes_sociais, historico) VALUES (12, 'Indiana Pacers', 'Estados Unidos', 'Indiana', 'Indianápolis', 1967, 'Conferência Leste', 'Divisão Central', 12, 'https://www.nba.com/pacers/',
'Twitter: https://twitter.com/Pacers
Facebook: https://www.facebook.com/Pacers
Instagram: https://www.instagram.com/pacers/
YouTube: https://www.youtube.com/user/Pacers
', 'O Indiana Pacers é uma equipe de basquete profissional americana sediada em Indianápolis, Indiana. A equipe foi fundada em 1967 como uma equipe de expansão da American Basketball Association (ABA) e se juntou à NBA em 1976. O Pacers tem uma longa história de sucesso, incluindo 31 aparições nos playoffs e 5 finais da NBA. A equipe é conhecida por ter tido alguns dos melhores jogadores da história da NBA, incluindo Reggie Miller, Mel Daniels e Roger Brown. O Pacers joga seus jogos em casa no Bankers Life Fieldhouse em Indianápolis desde 1999.');

INSERT INTO times(id, nome, pais_origem, estado_origem, cidade_origem, ano_fundacao, conferencia_nba, divisao_nba, arena_id, website_oficial, redes_sociais, historico) VALUES (13, 'Los Angeles Clippers', 'Estados Unidos',  'Califórnia', 'Los Angeles', 1970, 'Conferência Oeste', 'Divisão Pacífico', 13, 'https://www.nba.com/clippers/',
