INSERT INTO times(id, nome, pais_origem, estado_origem, cidade_origem, ano_fundacao, conferencia_nba, divisao_nba, arena_id, website_oficial, redes_sociais, historico) VALUES (14, 'Los Angeles Lakers', 'Estados Unidos', 'Califórnia', 'Los Angeles', 1947, 'Conferência Oeste', 'Divisão Pacífico', 14, 'https://www.nba.com/lakers', 
