INSERT INTO times(id, nome, pais_origem, estado_origem, cidade_origem, ano_fundacao, conferencia_nba, divisao_nba, arena_id, website_oficial, redes_sociais, historico) VALUES (15, 'Memphis Grizzlies', 'Estados Unidos', 'Tennessee', 'Memphis', 1995, 'Conferência Oeste', 'Divisão Sudoeste', 15, 'https://www.nba.com/grizzlies/',
