INSERT INTO times(id, nome, pais_origem, estado_origem, cidade_origem, ano_fundacao, conferencia_nba, divisao_nba, arena_id, website_oficial, redes_sociais, historico) VALUES (18, 'Minnesota Timberwolves', 'Estados Unidos', 'Minnesota', 'Minneapolis', 1989, 'Conferência Oeste', 'Divisão Noroeste', 18, 'https://www.nba.com/timberwolves/', 'Twitter: https://twitter.com/timberwolves
